// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract Animal {
    int8 private energy;

    constructor() {
        energy = 0;
    }

    function feedFood () internal{
        energy = energy + 5;
    }

    function feedWater () internal {
        energy = energy + 1;
    }

    function burnEnergy (int8 _burnEnergy) internal {
        require((energy - _burnEnergy) > 0, "Low energy: cannot do activity");
        energy = energy - _burnEnergy;
    }

    function displayEnergy () internal view returns (int8){
        return energy;
    }

}

contracts/Dog.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "./Animal.sol";

contract Dog is Animal {
    constructor(){}

    function eatDogFood() public {
        Animal.feedFood();
    }

    function eatWater() public {
        Animal.feedWater();
    }

    function run() public {
        Animal.burnEnergy(3);
    }

    function bark() public {
        Animal.burnEnergy(1);
    }

    function displayDogEnergy () public view returns (int8){
        return int8(Animal.displayEnergy());
    }
}

